mapper.select:
SELECT NWOPERATOR, NWTYPE, AVG(ULSPEED) AS SPEED FROM NWQUALITY
AND NWOPERATOR = #{nwOperator} AND NWTYPE = #{nwType} AND DAYTIME > #{startDate} AND DAYTIME < #{endDate}
GROUP BY NWOPERATOR, NWTYPE ORDER BY NWOPERATOR, NWTYPE

SELECT PROVINCE, NWOPERATOR, AVG(DLSPEED) AS SPEED FROM NWQUALITY
AND NWOPERATOR = #{nwOperator} AND DAYTIME > #{startDate} AND DAYTIME < #{endDate}
GROUP BY PROVINCE, NWOPERATOR ORDER BY PROVINCE ASC, NWOPERATOR ASC

SELECT
    SUBSTR(TO_CHAR(time_index,'#'), 7, 2) AS x_rate,
    AVG(DLSPEED) AS SPEED,
    NWOPERATOR,
FROM
    NWQUALITY

    AND NWOPERATOR = #{nwOperator}
    AND NWTYPE = #{nwType}
    AND LANDMARK = #{landmark}
    AND DAYTIME > #{startDate} 
    AND DAYTIME < #{endDate}
