mapper.select:
SELECT
    RSSI,user_lon,user_lat,network_type
FROM
    Signal_Strength



            AND NWOPERATOR  IN ('CMCC', 'CUCC', 'CTCC')


            AND NWOPERATOR = #{nwOperator}




            AND NETWORK_TYPE IN ('2G', '3G', '4G','WIFI')


             AND NETWORK_TYPE = #{nwType}


    AND TIME_INDEX > #{startDate} 
    AND TIME_INDEX < #{endDate}


limit 10

SELECT
TO_CHAR(time_index,'#') AS x_rate,
TO_CHAR(time_index/100,'#') AS x_rate,
TO_CHAR(time_index/10000,'#') AS x_rate,
NETWORK_NAME, AVG(RSSI) AS rssi FROM Signal_Strength
AND LANDMARK = #{landmark}
NETWORK_TYPE IN ('2G', '3G', '4G','WIFI')
NETWORK_TYPE = #{networkType}
AND TIME_INDEX > #{startDate} AND TIME_INDEX < #{endDate}
GROUP BY NETWORK_NAME, x_rate ORDER BY x_rate

SELECT LANDMARK, NETWORK_NAME, NETWORK_TYPE, AVG(RSSI) AS RSSI FROM Signal_Strength
TIME_INDEX > #{startDate} AND TIME_INDEX < #{endDate} AND NETWORK_NAME = #{networkName}
GROUP BY LANDMARK, NETWORK_NAME,NETWORK_TYPE ORDER BY LANDMARK, NETWORK_NAME,NETWORK_TYPE
